#include "CBoard.h"CBoard::CBoard() {    jBoard = NULL;    length  = 10;    level  = 10;    levelThread = new LevelThread(this);    levelThread->Start();}void CBoard::setJBoard(JBoardAdapter* jBrd) {    jBoard = jBrd;}void CBoard::setLength(int len) {    if (length != len) {        length = len;        printf("Length = %d\r\n", length);    }}int CBoard::getLength() {    return length;}void CBoard::levelChanged(int lev) {    if (level != lev) {        level = lev;        if (jBoard) {            jBoard->levelChanged(level);        }    }}LevelThread::LevelThread(CBoard* cBrd) {    cBoard = cBrd;}LevelThread::Run() {    while (1) {        Sleep(1000);        int level = rand() / (RAND_MAX / 10);        cBoard->levelChanged(level);    }    return 0;}