#include "DemoAdapter.h"#include "CBoard.h"JNIEXPORT jint JNICALL Java_DemoAdapter_createCBoard(JNIEnv *, jobject) {    return reinterpret_cast<jint>(new CBoard());}JNIEXPORT void JNICALL Java_DemoAdapter_setJBoard (JNIEnv * jEnv, jobject, jint cBrd, jobject jBrd) {    //JBoardAdapter 在初始化的时候，完成方法ID，全局变量jObject的初始化，以后在使用是不需要在初始化	(reinterpret_cast<CBoard*>(cBrd))->setJBoard(new JBoardAdapter(jEnv, jBrd));}