package com.stdying.adapter;/** * 在java和JNI中交换对象，在java中使用int存储一个jni C++中的一个指针对象 * */class DemoAdapter {    JBoard jBoard;    DemoAdapter() {        int cBoard = createCBoard();        jBoard = new JBoard(cBoard);        setJBoard(cBoard, jBoard);    }        public native int createCBoard();    public native void setJBoard(int cBoard, Object jBoard);    public static void main(String[] args) {        System.loadLibrary("DemoAdapter");        DemoAdapter da = new DemoAdapter();    }}